mapper.update:
UPDATE delivery_open_hour
SET StartTime = #{startTime},
	EndTime = #{endTime}
WHERE DeliveryCompanyID = #{deliveryCompanyID} 
  AND WeekDay = #{weekDay}
  AND Seq = #{seq}

mapper.delete:
DELETE FROM delivery_open_hour
WHERE DeliveryCompanyID = #{deliveryCompanyID} 
  AND WeekDay = #{weekDay}
  AND Seq = #{seq}
