mapper.select:
SELECT 

FROM order_menu_option 
WHERE OrderID = #{orderID} 
		AND RestaurantID = #{restaurantID}

mapper.update:
UPDATE 
	order_menu_option  
SET 
	OptionName = TRIM(#{optionName}),
	MenuID = #{menuID},
	Unit = #{unit},
	ExtraCharge = #{extraCharge}
WHERE OrderID = #{orderID} 
  AND RestaurantID = #{restaurantID}
  AND Seq = #{seq}
  AND OptionID = #{optionID}
